def solution(encrypted_text, key, rotation):
    answer = ''
    # print('encrypted_text',encrypted_text)
    # print(len(encrypted_text))

    if rotation < 0:
        nrot = -rotation % len(encrypted_text)
        nrot *= -1
    else:
        nrot = rotation % len(encrypted_text)
    # print('nrot',nrot)

    rstr = encrypted_text[nrot:] + encrypted_text[:nrot]
    # print('rstr',rstr)
    # print()

    nstr = ''
    for i in range(len(rstr)):
        ch = rstr[i]
        ch_num = ord(ch) - ord('a')
        chg = ord(key[i]) - ord('a') + 1

        nch_num = (26 + ch_num - chg) % 26
        # print(ch_num - (ord(key[i]) - ord('a') + 1))
        # print((ch_num - (ord(key[i]) - ord('a') + 1)) % 26)
        # print((26 + ch_num - (ord(key[i]) - ord('a') + 1)) % 26)
        # print('ch_num, chg, nch_num',ch_num, chg, nch_num)

        nch = chr(nch_num + ord('a'))
        nstr += nch
    # print(nstr)

    answer = nstr
    # print(len(answer))
    return answer



print(solution(encrypted_text, key, rotation))

